import fetch from 'node-fetch';
//                 32123    234525432 234535432
let decimalpi; //= "1 32123 45 234525432 234535432" 
let apart = "";
let index = 0;
let countres = 2;
let pldrnc = "";
let cifras = 7;

// let decimalpi = 

const URL = "https://api.pi.delivery/v1/pi?start=0&numberOfDigits=1000"; 
let settings = { method: "Get" };

fetch(URL, settings)
    .then(res => res.json())
    .then((json) => {
        decimalpi = json.content;
    })
    .then(() => countPPN(decimalpi));


const countPPN = (decimalpi) => {

    for (let x=0; x<decimalpi.length;x++) {
        
        // console.log("indice nÂ°"+x)
        // console.log("letra:"+decimalpi[x])
        
        if(decimalpi[x-countres]===decimalpi[x]){
            // console.log("valor de resta en: "+(x-countres));
            index=x; 
            pldrnc=decimalpi.slice(x-countres,x+1);
            // console.log("Numero palindromo:"+pldrnc);   
            countres = countres+2;  
            
            if(pldrnc.length===cifras){ console.log("tu numero palindromico de "+ cifras + " es "+pldrnc); }    
        }
        else {
            countres = 2;
        }
    }    
    return(console.log("it's finished"))
}



